import fs  from 'fs';
import path  from 'path';
import Logger from 'bem-site-logger';
import Checker  from '../checker';
import Util  from '../util';

const logger = Logger.setOptions({ level: 'info', useDate: false }).createLogger(module);

/**
 * Creates configuration directory inside process.cwd() folder
 */
export function createConfigsDir() {
    try {
        fs.mkdirSync(Util.getConfigurationDirectory());
    } catch (error) {}
}

/**
 * Creates stub configuration file with default params
 * @return {Object} stub config object
 */
export function createConfigStub() {
    return [
        'concurrent',
        'requestHeaders',
        'requestRetriesAmount',
        'requestTimeout',
        'acceptedSchemes',
        'checkExternalUrls',
        'excludeLinkPatterns'
    ].reduce((prev, item) => {
        prev[item] = Checker.DEFAULT[item];
        return prev;
    }, {
        url: 'http://my.site.com',
        logger: { level: 'info' }
    });
}

/**
 * Generates stub configuration file inside configuration folder
 * @param  {String} fileName name of file
 * @return {Boolean} returns true if success, false otherwise
 */
export function createConfigFile(fileName) {
    fileName = fileName.replace(/\//g, '') + '.js';

    createConfigsDir();

    fs.writeFileSync(path.join(Util.getConfigurationDirectory(), fileName),
            'module.exports = ' + JSON.stringify(createConfigStub(), null, 4), 'utf-8');
    logger.info('Configuration file: => %s has been generated successfully', fileName);
    return true;
}

import path from 'path';
import Logger from 'bem-site-logger';
import Checker  from '../checker';
import ReporterJson from '../reporters/json';
import ReporterHtml from '../reporters/html';

const logger = Logger.setOptions({ level: 'info', useDate: false }).createLogger(module);

/**
 * Runs application from cli interface
 * @param {Object}    [options]                            configuration object
 * @param {String}    [options.mode]                       mode of checking ("website", "section" or "page")
 * @param {Number}    [options.concurrent]                 number of concurrent requests
 * @param {Number}    [options.requestRetriesAmount]       number of attempts for request if it fails at first
 * @param {Number}    [options.requestTimeout]             request timeout (in milliseconds)
 * @param {Boolean}   [options.checkExternalUrls]          set `true` for check outer links
 * @trows Error
 */
export function run (options) {
    var configFileName = options.config,
        config;

    try {
        config = require(path.join(process.cwd(), configFileName));
    } catch (error) {
        throw new Error('Configuration file not found');
    }

    ['concurrent', 'requestRetriesAmount', 'requestTimeout', 'checkExternalUrls', 'mode'].forEach(item => {
        if (options[item]) {
            config[item] = options[item];
        }
    });

    config.onDone = (statistic) => {
        logger.info('finish to analyze pages');

        logger
            .info('-- Internal urls: [%s]', statistic.getInternalCount())
            .info('-- External urls: [%s]', statistic.getExternalCount())
            .info('-- Broken urls: [%s]', statistic.getBrokenCount())
            .info('-- Total urls: [%s]', statistic.getAllCount())
            .info('-- Broken urls percentage: [%s] %', (statistic.getBrokenCount() * 100) / statistic.getAllCount());

        var reporters = [
            new ReporterJson(options),
            new ReporterHtml(options)
        ];

        reporters.map(item => {
            return item.createReport(path.basename(configFileName, '.js'), statistic, config);
        });
    };

    (new Checker(config)).start(options.url || config.url);
}

import Logger  from 'bem-site-logger';

/**
 * @exports
 * @class Base
 * @desc Base class for application modules
 */
export default class Base {
    /**
     * Constructor function
     * @param  {Object} options object
     * @param  {Module} module - nodejs module instance
     * @return {Base}
     */
    constructor (options, module) {
        options = options || {};
        var logOpts = options['logger'] || { level: 'debug' };
        logOpts.useDate = false;

        /**
         * Instance of class logger
         * @param {Object} loggerOptions - log settings
         */
        this._logger = Logger.setOptions(logOpts).createLogger(module);
    }
}

import _ from 'lodash';
import Base from './base';

/**
 * @exports
 * @class BasedOption
 * @desc Implements options set and get logic
 */
export default class BasedOption extends Base {
    /**
     * Sets value to options model for given option field name
     * @param {Object} options      — configuration object
     * @param {String} name         - name of option field
     * @param {*}      defaultValue - option default value
     * @returns {BasedOption}
     * @public
     */
    setOption (options, name, defaultValue) {
        /**
         * Options hash
         * @type {Object}
         */
        this._options = this._options || {};
        this._options[name] = options[name] || defaultValue;
        if (!_.isFunction(this._options[name]) && !_.isObject(this._options[name])) {
            this._logger.info('Set option [%s] => %s', name, this._options[name]);
        }
        return this;
    }

    /**
     * Returns option value by given option name
     * @param {String} name of option
     * @returns {*}
     * @public
     */
    getOption (name) {
        return this._options[name];
    }
}

import _ from 'lodash';
import got  from 'got';
import Base  from './base';
import BasedOptions  from './based-option';
import Model from './model/model';
import Document  from './model/document';
import Statistic  from './model/statistic';
import LinkAnalyzer from './link-analyzer';

require('http').globalAgent.maxSockets = Infinity;
require('https').globalAgent.maxSockets = Infinity;
process.env.NODE_TLS_REJECT_UNAUTHORIZED = "0";

export default class Checker extends Base {
    /**
     * Constructor
     * @param {Object}    [options]                            — configuration object
     * @param {String}    [options.mode]                       - mode of checking ("website", "section" or "page")
     * @param {Number}    [options.concurrent]                 — number of concurrent requests
     * @param {Object}    [options.requestHeaders]             — set custom request headers for crawler requests
     * @param {Number}    [options.requestRetriesAmount]       - number of attempts for request if it fails at first
     * @param {Number}    [options.requestTimeout]             - request timeout (in milliseconds)
     * @param {Function}  [options.onDone]                     - set custom done handler function
     * @param {String[]}  [options.acceptedSchemes]            — set array of accepted request acceptedSchemes
     * @param {Boolean}   [options.checkExternalUrls]          — set `true` for check outer links
     * @param {RegExp[]}  [options.excludeLinkPatterns         - array of regular expressions. Urls that matches
     * for this regular expressions would be excluded from verification
     * @constructor
     */
    constructor(options = {}) {
        super(options, module);

        this.logger.info('Initialize crawler instance');

        /**
         * Checker options
         * @type {BasedOptions}
         */
        this._options = new BasedOptions();

        const def = this.constructor.DEFAULT;
        this.options
            .setOption(options, 'mode', def.mode)
            .setOption(options, 'concurrent', def.concurrent)
            .setOption(options, 'requestHeaders', def.requestHeaders)
            .setOption(options, 'requestTimeout', def.requestTimeout)
            .setOption(options, 'acceptedSchemes', def.acceptedSchemes)
            .setOption(options, 'checkExternalUrls', def.checkExternalUrls)
            .setOption(options, 'excludeLinkPatterns', def.excludeLinkPatterns)
            .setOption(options, 'requestRetriesAmount', def.requestRetriesAmount)
            .setOption(options, 'onDone', this.onDone.bind(this));
    }

    /**
     * Getter function for options
     * @returns {BasedOptions}
     */
    get options() {
        return this._options;
    }

    /**
     * Returns logger instance
     * @return {Logger} logger
     */
    get logger() {
        return this._logger;
    }

    /**
     * Returns model instance
     * @return {Model} model
     */
    get model() {
        return this._model;
    }

    /**
     * Returns instance of LinkAnalyzer class
     * @return {LinkAnalyzer} linkAnalyzer
     */
    get linkAnalyzer() {
        return this._linkAnalyzer;
    }

    /**
     * Returns instance of Statistic class
     * @return {Statistic} statistic
     */
    get statistic() {
        return this._statistic;
    }

    /**
     * Sets model instance
     * @param {Model} model instance
     * @return {Checker}
     */
    initModel(model) {
        this._model = model;
        return this;
    }

    /**
     * Sets linkAnalyzer instance
     * @param {LinkAnalyzer} linkAnalyzer
     * @return {Checker}
     */
    initLinkAnalyzer(linkAnalyzer) {
        this._linkAnalyzer = linkAnalyzer;
        return this;
    }

    /**
     * Sets Statistic instance
     * @param  {Statistic} statistic
     * @return {Checker}
     */
    initStatistic(statistic) {
        this._statistic = statistic;
        return this;
    }

    /**
     * Returns application default options
     * @returns {Object}
     * @constructor
     */
    static get DEFAULT() {
        return {
            mode: 'website',
            concurrent: 100,
            requestHeaders: { 'user-agent': 'node-spider' },
            requestRetriesAmount: 5,
            requestTimeout: 5000,
            acceptedSchemes: ['http:', 'https:'],
            checkExternalUrls: false,
            excludeLinkPatterns: []
        };
    }

    /**
     * Returns application constants model
     * @returns {Object}
     * @static
     */
    static get CONSTANTS() {
        return {
            URL_REGEXP: /https?\:\/\/\w+((\:\d+)?\/\S*)?/,
            MODE: {
                WEBSITE: 'website',
                SECTION: 'section',
                PAGE: 'page'
            }
        };
    }

    /**
     * Processes loaded document
     * @param {Document}                   document - document model
     * @param {String}                     document.url - request url
     * @param {HttpResponse|HttpsResponse} document.res - response object
     * @protected
     */
    processLoadedDocument(document) {
        var _this = this,
            documentUrl = document.url,
            $ = document.$;

        $('a').each(function () {
            var href = $(this).attr('href');

            if (href) {
                let url = document.resolve(href.split('#')[0]);

                if (_this.linkAnalyzer.isNeedToSkipUrl(url, documentUrl)) {
                    return;
                }

                if(_this.linkAnalyzer.isExternal(url)) {
                    _this.model.addToExternal(url, documentUrl, href);
                } else {
                    _this._addToQueue(url, { page: documentUrl, href: href });
                }
            }
        });
        this._onFinishLoad(documentUrl);
    }

    /**
     * Start to crawl pages for given url
     * @param {String} url - initial site url for start
     * @throws Error
     * @public
     */
    start(url) {
        if (!url) {
            throw new Error('Url was not set');
        }

        if (!url.match(this.constructor.CONSTANTS.URL_REGEXP)) {
            throw new Error('Urls is not valid');
        }

        this
            .initStatistic(new Statistic())
            .initModel(new Model())
            .initLinkAnalyzer(new LinkAnalyzer(url, this.options))
            .logger
            .info('Start to analyze pages for: => %s', url)
            .info('It can be take a long time. Please wait ...');
        this._addToQueue(url, { page: url });
    }

    /**
     * onDone callback function
     * @param {Statistic} statistic model instance
     * @protected
     */
    onDone(statistic) {
        return statistic;
    }

    /**
     * Makes request to given external url
     * @param {String} url - external url (url that should be requested)
     * @param {Object} advanced - object with advanced data
     * @param {Number} attempt - number of request attempt
     * @private
     */
    _checkInternalLink(url, advanced, attempt = 0) {
        if (attempt === 0) {
            this.model.addToActive(url);
        }

        got.get(url, this._getRequestOptions(), (error, data, res) => {
            if (error) {
                 if (!error.statusCode && attempt < this.options.getOption('requestRetriesAmount')) {
                     return this._checkInternalLink(url, advanced, ++attempt);
                 } else {
                     this.statistic.increaseInternalCount();
                     this.statistic.getBroken().add(url, advanced, error.statusCode);
                     this.logger.warn('Broken [%s] link: => %s on page: => %s',
                         error.statusCode, advanced.href, advanced.page);
                 }
                 return this._onFinishLoad(url);
            }

            this.logger.debug('[%s] [%s] Receive [%s] for url: => %s',
                this.model.getPendingLength(), this.model.getActiveLength(), res ? res.statusCode : -1, url);

            this.statistic.increaseInternalCount();
            this.processLoadedDocument(new Document(url, data));
        });
    }

    /**
     * Checks given external link item
     * @param {Object} item - external link item object
     * @param {String} item.url - external link url
     * @param {Object} item.advanced - external link advanced meta data object
     * @param {Number} attempt - number of request attempt
     * @returns {Promise}
     * @private
     */
    _checkExternalLink(item, attempt = 0) {
        var url = item.url,
            advanced = item.advanced;

        function ping() {
            return new Promise(resolve => {
                got.head(url, this._getRequestOptions(), (error, data, res) => {
                    if (error) {
                        if (!error.statusCode && attempt < this.options.getOption('requestRetriesAmount') - 1) {
                            return resolve(false);
                        } else if (error.statusCode) {
                            this.statistic.getBroken().add(url, advanced, error.statusCode);
                            this.logger.warn('Broken [%s] link: => %s on page: => %s',
                                error.statusCode, advanced.href, advanced.page);
                        }
                    }

                    this.logger.debug('[%s] [%s] Receive [%s] for url: => %s',
                        this.model.getPendingLength(), this.model.getActiveLength(), res ? res.statusCode : -1, url);

                    this.statistic.increaseExternalCount();
                    resolve(true);
                });
            });
        }

        return ping.apply(this).then(result => {
            return result || this._checkExternalLink(item, ++attempt);
        });
    }

    /**
     * Check all collected external links
     * @returns {Promise}
     * @private
     */
    _checkExternalLinks() {
        if (!this.model.areExternal()) {
            return Promise.resolve();
        }

        this.logger.info('Start to verify external links ...');

        return _(Array.from(this.model.external))
            .map(item => {
                return { url: item[0], advanced: item[1] };
            })
            .chunk(100)
            .value()
            .reduce((prev, portion) => {
                return prev.then(() => {
                    return Promise.all(portion.map(this._checkExternalLink.bind(this)));
                });
            }, Promise.resolve());
    }

    /**
     * Adds item to check queue
     * @param {String} url - link url
     * @param {Object} advanced - object with advanced data
     * @private
     */
    _addToQueue(url, advanced) {
        url = url.replace(/\/$/, '');

        if (this.model.addToProcessed(url)) {
            this.model.isQueueFull(this.options.getOption('concurrent')) ?
                this.model.addToPending(url, advanced) :
                this._checkInternalLink(url, advanced);
        }
    }

    /**
     * Function which called after request to given url will be finished
     * @param  {String} url which was requested
     * @return {*}
     * @private
     */
    _onFinishLoad(url) {
        this.model.removeFromActive(url);
        if (!this.model.isQueueFull(this.options.getOption('concurrent'))) {
            var next = this.model.removeFromPending();
            if (next) {
                this._checkInternalLink(next.url, next.advanced);
            } else if (!this.model.areActive()) {
                return this._checkExternalLinks().then(() => {
                    this.options.getOption('onDone')(this.statistic);
                });
            }
        }
    }

    /**
     * Returns request options hash
     * @returns {{encoding: string, headers: *, timeout: *}}
     * @private
     */
    _getRequestOptions() {
        return {
            encoding: 'utf-8',
            headers: this.options.getOption('requestHeaders'),
            timeout: this.options.getOption('requestTimeout')
        };
    }
}

import * as action from '../acts/config';

export default function () {
    return this
        .title('Config initialization command')
        .helpful()
        .opt()
            .name('name')
            .title('Name of configuration file')
            .short('n').long('name')
            .req()
            .end()
        .act((opts) => {
            action.createConfigFile(opts.name);
        });
}

import Logger from 'bem-site-logger';
const logger = Logger.setOptions({ level: 'info', useDate: false }).createLogger(module);

export default function () {
    return this
        .title('Show tool version command')
        .act(() => {
            var p = require('../../package.json');
            logger
                .info('Application name: => %s', p['name'])
                .info('Application version: => %s', p.version);
            return void 0;
        });
}

'use strict';

import commandConfig from './cmd-config';
import commandRun from './cmd-run';
import commandVersion from './cmd-version';

function command() {
    return require('coa').Cmd()
        .name(process.argv[1])
        .title('Broken links checker tool')
        .helpful()
        .cmd().name('config').apply(commandConfig).end()
        .cmd().name('run').apply(commandRun).end()
        .cmd().name('version').apply(commandVersion).end()
        .completable();
}

export default command();

import _ from 'lodash';
import RoutePattern from 'route-pattern';
import Url from 'url';
import Checker from './checker';

/**
 * @class LinkAnalyzer
 * @desc Hold logic about recursive links processing
 */
export default class LinkAnalyzer {
    /**
     * Initialize predefined skip rules for prevent deeper crawling for given url
     * @param {String} initial url
     * @param {Object} options object
     * @constructor
     */
    constructor(initial, options) {
        /**
         * Parsed url object from Url.parse
         * @param  {Object} initial url - base (root) url where analyze started from
         * @return {Object} options object
         */
        this._url = Url.parse(initial);
        this._options = options;
    }

    /**
     * Returns options model
     * @returns {BasedOption}
     */
    get options() {
        return this._options;
    }

    /**
     * Returns initial url as parsed url string via Url module
     * @returns {Object}
     */
    get url() {
        return this._url;
    }

    /**
     * Returns true if anyone of skip conditions returns true
     * @param {String} url url of link
     * @param {String} baseUrl url of page where link is
     * @returns {boolean} result flag
     * @public
     */
    isNeedToSkipUrl(url, baseUrl) {
        return [
            '_skipNonAcceptableProtocols',
            '_skipExternalUrls',
            '_skipExcludedUrls',
            '_skipOnMode'
        ].some(fName => {
            return this[fName](url, baseUrl);
        });
    }

    /**
     * Checks if given url is external
     * @param  {String}  url request url
     * @return {boolean} true if url is external. false otherwise
     * @public
     */
    isExternal(url) {
        return url.indexOf(this._url['hostname']) < 0;
    }

    /**
     * Check if protocol of given url satisfies acceptedSchemes criteria
     * @param {String} url request url
     * @returns {boolean} result flag
     * @private
     */
    _skipNonAcceptableProtocols(url) {
        return this._options.getOption('acceptedSchemes').indexOf(Url.parse(url).protocol) < 0;
    }

    /**
     * Checks if given url has the different hostname then initial
     * (If 'checkExternalUrls'rule is set to true)
     * @param {String} url request url
     * @returns {boolean} result flag
     * @private
     */
    _skipExternalUrls(url) {
        return !this._options.getOption('checkExternalUrls') && url.indexOf(this._url['hostname']) < 0;
    }

    /**
     * Checks if given url has host different then host of initial url
     * @param {String} url request
     * @returns {boolean} result flag
     * @private
     */
    _skipExcludedUrls(url) {
        return this._options.getOption('excludeLinkPatterns').some(pattern => {
            return _.isRegExp(pattern) ? pattern.test(url) : RoutePattern.fromString(pattern).matches(url);
        });
    }

    /**
     * Checks if given url is need to be check depending on mode configuration option
     * @type {boolean} result flag
     * @private
     */
    _skipOnMode(url, baseUrl) {
        var mode = this._options.getOption('mode'),
            baseUrlParsed = Url.parse(baseUrl);

        const MODES = Checker.CONSTANTS.MODE;
        if (mode === MODES.PAGE) {
            return baseUrlParsed.path !== this._url.path;
        } else if (mode === MODES.SECTION) {
            return baseUrlParsed.path.indexOf(this._url.path) === -1;
        } else {
            return false;
        }
    }
}

/**
 * @exports
 * @class BrokenUrls
 * @desc Model for holding broken links information and methods for working with this data
 */
export default class BrokenUrls {
    /**
     * Constructor function
     * @constructor
     */
    constructor() {
        /**
         * Array of broken urls infos
         * @type {Array}
         */
        this._urls = [];
    }

    /**
     * Static initializer for BrokenLinks instance
     * @returns {BrokenLinks}
     * @static
     */
    static create() {
        return new BrokenUrls();
    }

    /**
     * Add url with given response status code to model of broken links
     * @param {String} url - link url
     * @param {Obect} advanced - advanced data hash
     * @param {Number} code - response status code
     * @returns {exports}
     * @public
     */
    add(url, advanced, code) {
        this._urls.push({ url: url, advanced: advanced, code: code });
        return this;
    }

    /**
     * Return all broken url items
     * @returns {Object[]}
     * @public
     */
    getAll() {
        return this._urls;
    }

    /**
     * Return broken url items with given status code
     * @param {Number} code - response status code
     * @returns {Object[]}
     * @public
     */
    getByCode(code) {
        return this.getAll().filter(item => {
            return item.code === code;
        });
    }

    /**
     * Returns broken urls with response status equals to 404. Page not found error
     * @returns {Object[]}
     * @public
     */
    get404() {
        return this.getByCode(404);
    }

    /**
     * Returns broken urls with response status equals to 500. Server error
     * @returns {Object[]}
     * @public
     */
    get500() {
        return this.getByCode(500);
    }

    /**
     * Clears broken links model
     * @returns {exports}
     * @public
     */
    clear() {
        this._urls = [];
        return this;
    }
}

import cheerio  from 'cheerio';
import Url  from 'url';

/**
 * @exports
 * @class Document
 * @desc Model for loaded document from link url request
 */
export default class Document {
    /**
     * Constructor function
     * @param {String} url - request url
     * @param {HttpResponse} res - response object
     * @constructor
     */
    constructor (url, res) {
        /**
         * Response object
         * @type {HttpResponse|HttpsResponse} - respose object
         */
        this._res = res;

        /**
         * Url document has been loaded from
         * @type {String}
         */
        this._url = url;

        /**
         * Cheerio
         * @param  {String} res.toString('utf-8') response html data string
         * @return {Function} cherio tree function
         */
        try {
            this._$ = cheerio.load(res.toString('utf-8'));
        } catch (error) {
            this._$ = cheerio.load('');
        }
    }

    /**
     * Returns response object
     * @returns {HttpResponse|HttpsResponse}
     * @public
     */
    get res() {
        return this._res;
    }

    /**
     * Returns document url
     * @returns {String}
     * @public
     */
    get url() {
        return this._url;
    }

    /**
     * Parses response body parsed by cheerio
     * @returns {*|any|Object|undefined}
     * @public
     */
    get $() {
        return this._$;
    }

    /**
     * Resolves parsed link against document url
     * @param {String} link - document link
     * @public
     */
    resolve(link) {
        return Url.resolve(this._url, link);
    }
}

/**
 * @exports
 * @desc
 * Main checker model class
 */
export default class Model {
    constructor () {
        /**
         * Pending queue
         * @type {Array}
         */
        this._pending = [];

        /**
         * Performed items
         * @type {Array}
         */
        this._active = [];

        /**
         * Map of external link items
         * @type {Map}
         */
        this._external = new Map();

        /**
         * Map of processed link items
         * @type {Map}
         */
        this._processed = new Map();
    }

    /**
     * Getter for external links items map
     * @return {Map} external items map
     */
    get external() {
        return this._external;
    }

    /**
     * Add item to external links model
     * @param {String} url resolved from href
     * @param {String} baseUrl page url where href was discovered
     * @param {String} href found in document
     * @returns {Model}
     */
    addToExternal(url, baseUrl, href) {
        this._external.set(url, { page: baseUrl, href: href });
        return this;
    }

    /**
     * Add link url to list of active (checked at this moment)
     * @param {String} url of href found on page
     */
    addToActive(url) {
        this._active.push(url);
        return this;
    }

    /**
     * Adds item to array of pending items
     * @param {String} url      resolved url of link
     * @param {Object} advanced info object
     * @returns {Model}
     */
    addToPending(url, advanced) {
        this._pending.push({ url: url, advanced: advanced });
        return this;
    }

    /**
     * Returns length of active items array
     * @return {Number}
     */
    getActiveLength() {
        return this._active.length;
    }

    /**
     * Returns length of pending items array
     * @return {Number}
     */
    getPendingLength() {
        return this._pending.length;
    }

    /**
     * Returns true if external items map contains items
     * @return {Boolean}
     */
    areExternal() {
        return !!this._external.size;
    }

    /**
     * Returns true if active items array contains items
     * @return {Boolean}
     */
    areActive() {
        return !!this.getActiveLength();
    }

    /**
     * Returns true of length of active items greater then given max number
     * @param  {[type]}  max [description]
     * @return {Boolean}     [description]
     */
    isQueueFull(max) {
        return this._active.length >= max;
    }

    /**
     * Returns true if items was not at provessed items
     * Also item is put into processed map
     * @param {String} url resolved from link
     */
    addToProcessed(url) {
        if (this._processed.has(url)) {
            return false;
        }
        this._processed.set(url, true);
        return true;
    }

    /**
     * Removes item corresponded to given url from active items array
     * @param  {String} url resolved from link
     * @return {Model}
     */
    removeFromActive(url) {
        this._active.splice(this._active.indexOf(url), 1);
        return this;
    }

    /**
     * Removes first item from pending items array and returns it
     * @return {Object}
     */
    removeFromPending() {
        return this._pending.shift();
    }
}

import Broken  from './broken';

/**
 * @exports
 * @class Statistic
 * @desc Model for collection analyze results
 */
export default class Statistic {

    /**
     * Constructor function
     * @constructor
     */
    constructor() {
        /**
         * Brokens links model
         * @return {Broken}
         */
        this._broken = Broken.create();

        /**
         * Number of processed external links
         * @type {Number}
         */
        this._countExternal = 0;

        /**
         * Number of processed internal links
         * @type {Number}
         */
        this._countInternal = 0;
    }

    /**
     * Static constructor for Statistic class
     * @return {Statistic}
     * @static
     */
    static create() {
        return new Statistic();
    }

    /**
     * Increase count of processed internal website links
     * @returns {Statistic}
     * @public
     */
    increaseInternalCount() {
        this._countInternal++;
        return this;
    }

    /**
     * Increase count of processed external website links
     * @returns {Statistic}
     * @public
     */
    increaseExternalCount() {
        this._countExternal++;
        return this;
    }

    /**
     * Returns amount of processed internal website links
     * @returns {Number}
     * @public
     */
    getInternalCount() {
        return this._countInternal;
    }

    /**
     * Returns amount of processed external website links
     * @returns {Number}
     * @public
     */
    getExternalCount() {
        return this._countExternal;
    }

    /**
     * Returns total amount of processed links (internal and external)
     * @returns {Number}
     * @public
     */
    getAllCount() {
        return this.getExternalCount() + this.getInternalCount();
    }

    /**
     * Returns model of broken links
     * @returns {Broken}
     * @public
     */
    getBroken() {
        return this._broken;
    }

    /**
     * Returns amount of founded broken links
     * @returns {Number}
     * @public
     */
    getBrokenCount() {
        return this.getBroken().getAll().length;
    }
}

import fs from 'fs';
import path from 'path';
import Logger from 'bem-site-logger';
import Util from '../util';

/**
 * @class ReporterBase
 * @desc Base reporter class
 */
export default class ReporterBase {

    /**
     * constructor
     * @param  {Object} reporter options
     */
    constructor(options = {}) {
        options.logger = options.logger || {
            level: 'info'
        };
        options.logger.useDate = false;

        /**
         * Logger instance
         * @param {Logger} options.logger
         */
        this._logger = Logger.setOptions(options.logger).createLogger(module);
    }

    /**
     * Creates reports folder in current work directory
     * @return void 0
     * @protected
     */
    createReportsFolder() {
        try {
            fs.mkdirSync(Util.getReportsDirectory());
        } catch (error) {}
    }

    /**
     * Creates folder named as given configurationName inside reports folder
     * @param  {String} configurationName name of configuration file
     * @return void 0
     * @protected
     */
    createReportFolder(configurationName) {
        this.createReportsFolder();
        try {
            fs.mkdirSync(path.join(Util.getReportsDirectory(), configurationName));
        } catch (error) {}
    }

    /**
     * Saves report to file
     * @param {String} configurationName name of configuration
     * @param {String} type of report
     * @param {String} content of report
     * @param {String} date formatted
     * @returns {Promise}
     */
    saveReportFile(configurationName, type, content, date) {
        this.createReportFolder(configurationName);
        var fileName = `${date}.${type}`,
            filePath = path.join(Util.getReportsDirectory(), configurationName, fileName);

        fs.writeFileSync(filePath, content, { encoding: 'utf-8' });
        this._logger.info('Report saved: %s', filePath);
    }
}

